import React, { useState } from "react";

const App = () => {
    const [cdh,sdh]=useState("");
    const [name,sname]=useState();
    const [lname,slname]=useState();
    const [name2,sname2]=useState("");
 const Cl =(e) =>{
    e.preventDefault(); 
    //var temp = document.getElementById("data").value.toUpperCase();
    //console.log(cdh);
    sname(cdh);
    sname2(lname);
 }
 const abc = (event) => {
       sdh(event.target.value);
 }
 const abc1 = (event) => {
    slname(event.target.value);
}

    return (
      <><div className="main_div">
          <form onSubmit={Cl}>
          <div>
              <h1>Hello {name} {name2}</h1>
              <input id="data" type='text' placeholder="Enter Your First Name" onChange={abc} value={cdh}/>
              <input id="data2" type='text' placeholder="Enter Your Last Name" onChange={abc1} value={lname}/>
              { <button type="submit">click here To Submit</button> }
          </div>
          </form>
          </div>
      </>
    );
}
export default App;
