import React, { useState } from "react";

const App = () => {
    const[fullName,SfullName] =useState({fname:" ",lname:" "});   
 const Cl =(e) =>{
     alert("Form Submited");
    e.preventDefault(); 
    //var temp = document.getElementById("data").value.toUpperCase();
    //console.log(fname);

 }
 const inputEvent = (event) => {
     console.log(event.target.value)
       SfullName.fname(document.getElementsByName("fname"));
       SfullName.lname(document.getElementsByName("lname"));
 }


    return (
      <><div className="main_div">
          <form onSubmit={Cl}>
          <div>
              <h1>Hello</h1>
              <input id="data" type='text' placeholder="Enter Your First Name" name="fname" onChange={inputEvent} value={fullName.fname}/>
              <input id="data2" type='text' placeholder="Enter Your Last Name" name="lname" onChange={inputEvent} value={fullName.lname}/>
              { <button type="submit">click here To Submit</button> }
          </div>
          </form>
          </div>
      </>
    );
}
export default App;
